{"nbformat":4,"nbformat_minor":0,"metadata":{"colab":{"provenance":[],"authorship_tag":"ABX9TyN9p1CeoCkQR/HnYjmoCigO"},"kernelspec":{"name":"python3","display_name":"Python 3"},"language_info":{"name":"python"}},"cells":[{"cell_type":"code","execution_count":null,"metadata":{"id":"2jq0ZNxZY8MV"},"outputs":[],"source":["# Step 1: Import Libraries and Load the Model\n","import numpy as np\n","import tensorflow as tf\n","from tensorflow.keras.datasets import imdb\n","from tensorflow.keras.preprocessing import sequence\n","from tensorflow.keras.models import load_model\n","\n","# Load the IMDB dataset word index\n","word_index = imdb.get_word_index()\n","reverse_word_index = {value: key for key, value in word_index.items()}\n","\n","# Load the pre-trained model with ReLU activation\n","model = load_model('simple_rnn_imdb.keras')\n","\n","# Step 2: Helper Functions\n","# Function to decode reviews\n","def decode_review(encoded_review):\n","    return ' '.join([reverse_word_index.get(i - 3, '?') for i in encoded_review])\n","\n","# Function to preprocess user input\n","def preprocess_text(text):\n","    words = text.lower().split()\n","    encoded_review = [word_index.get(word, 2) + 3 for word in words]\n","    padded_review = sequence.pad_sequences([encoded_review], maxlen=500)\n","    return padded_review\n","\n","## Design streamlit app\n","import streamlit as st\n","\n","# Streamlit app name\n","st.title('IMDB Movie Review Sentiment Analysis')\n","st.write('Enter a movie review to classify it as positive or negative.')\n","\n","# User input\n","user_input = st.text_area('Movie Review')\n","\n","if st.button('Classify'):\n","\n","    preprocessed_input=preprocess_text(user_input)\n","\n","    ## MAke prediction\n","    prediction=model.predict(preprocessed_input)\n","    sentiment='Positive' if prediction[0][0] > 0.5 else 'Negative'\n","\n","    # Display the result\n","    st.write(f'Sentiment: {sentiment}')\n","    st.write(f'Prediction Score: {prediction[0][0]}')\n","else:\n","    st.write('Please enter a movie review.')\n"]}]}